import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatNativeDateModule } from '@angular/material/core';
import { FormBuilder, FormGroup, ReactiveFormsModule } from '@angular/forms';
import { MatExpansionModule } from '@angular/material/expansion';
import { NgxLoadingModule } from 'ngx-loading';
import { MatCardModule } from '@angular/material/card';
import { MatFormFieldModule } from '@angular/material/form-field';
import { UserService } from '../../_shared/user.service';
import { animate, state, style, transition, trigger } from '@angular/animations';
import { MatIconModule } from '@angular/material/icon';
import { MatTableModule } from '@angular/material/table';


export interface PeriodicElement {
  firstName: string;
  gender: string;
  maidenName: string;
  phone: string;
}

const ELEMENT_DATA: PeriodicElement[] = [
  {
    firstName: "Hydrogen",
    gender: "hjgfh",
    maidenName: "H",
    phone: `Hydrogen is a chemical element with symbol H and atomic number 1. With a standard
        atomic weight of 1.008, hydrogen is the lightest element on the periodic table.`,
  },
]
@Component({
  selector: 'app-users',
  standalone: true,
  imports: [CommonModule, MatNativeDateModule,
    ReactiveFormsModule, MatExpansionModule, NgxLoadingModule, MatCardModule, MatFormFieldModule, MatIconModule, MatTableModule],
  templateUrl: './users.component.html',
  styleUrls: ['./users.component.scss'],
  animations: [
    trigger("detailExpand", [
      state("collapsed", style({ height: "0px", minHeight: "0" })),
      state("expanded", style({ height: "*" })),
      transition(
        "expanded <=> collapsed",
        animate("225ms cubic-bezier(0.4, 0.0, 0.2, 1)")
      ),
    ]),
  ],
})
export class UsersComponent implements OnInit {
  user!: FormGroup
  currentlyOpenedItemIndex = -1;
  loading: boolean = false;
  usersData: any
  uservalue: any;
  constructor(private fb: FormBuilder, private userApi: UserService) {
  }

  ngOnInit(): void {
    this.userForm();
    this.getUser();
  }

  dataShow(row: any) {
    row.isExpand = !row.isExpand
    this.user.patchValue({
      firstName: this.row.firstName,
      maidenName: this.row.lastName,
      gender: this.row.gender,
      phone: this.row.phone
    })

  }

  userForm() {
    this.user = this.fb.group({
      firstName: [],
      gender: [],
      maidenName: [],
      phone: [],
    })
  }

  getUser() {
    this.userApi.get().subscribe(res => {
      this.usersData = res
      for (var user of this.usersData.users) {
        this.uservalue = user
        console.log(this.uservalue)
      }
    })
  }

  showSpinner() {
    this.loading = true;
    setTimeout(() => { this.loading = false; }, 2000);
  }
}
<div class="table-responsive container ">
    <h1 class="text-center">User Management</h1>
    <table class="table table-bordered">
        <thead>
            <tr>
                <th>Action</th>
                <th>First Name</th>
                <th>Last Name</th>
                <th>Gender</th>
                <th>phone</th>
            </tr>
        </thead>
        <tbody>
            <ng-container *ngFor="let row of usersData.users">
                <tr>
                    <td (click)="dataShow(row)">
                        <i *ngIf="row.isExpand" class="bi bi-chevron-down"></i>
                        <i *ngIf="!row.isExpand" class="bi-chevron-up"></i>
                    </td>
                    <td>{{ row.firstName }}</td>
                    <td>{{ row.lastName }}</td>
                    <td>{{ row.gender }}</td>
                    <td>{{ row.phone }}</td>

                </tr>
                <tr *ngIf="row.isExpand">
                    <td colspan="4">
                        <form action="" [formGroup]="user">
                            <div class="row">
                                <div class="col-md-6">
                                    <label for="">First Name</label>
                                    <input type="text" class="form-control" formControlName="firstName">
                                </div>
                                <div class="col-md-6">
                                    <label for="">Last Name</label>
                                    <input type="text" class="form-control" formControlName="maidenName">
                                </div>
                                <div class="col-md-6">
                                    <label for="">Gender</label>
                                    <input type="text" class="form-control" formControlName="gender">
                                </div>
                                <div class="col-md-6">
                                    <label for="">Contact</label>
                                    <input type="text" class="form-control" formControlName="phone">
                                </div>
                                <div class="col-md-3 mt-3">
                                    <button class="btn btn-primary" [disabled]="loading" (click)="showSpinner()"><i class="fa"
                                            [ngClass]="{'fa-spin fa-spinner': loading,}"></i> Submit </button>&nbsp;&nbsp;
                                    <button class="btn btn-dark">Cancel</button>
                                </div>
                            </div>

                        </form>
                    </td>
                </tr>
            </ng-container>
        </tbody>
    </table>
</div>
