import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatNativeDateModule } from '@angular/material/core';
import { FormBuilder, FormGroup, ReactiveFormsModule } from '@angular/forms';
import { MatExpansionModule } from '@angular/material/expansion';
import { NgxLoadingModule } from 'ngx-loading';
import { MatCardModule } from '@angular/material/card';
import { MatFormFieldModule } from '@angular/material/form-field';
import { UserService } from '../../_shared/user.service';
import { animate, state, style, transition, trigger } from '@angular/animations';
import {MatIconModule} from '@angular/material/icon';
import {MatTableModule} from '@angular/material/table';


export interface PeriodicElement {
  firstName: any;
  gender: any;
  maidenName: any;
  phone: any;
}

const ELEMENT_DATA: PeriodicElement[] = [
  // {
  //   column1: "Hydrogen",
  //   column2: "hjgfh",
  //   column3: "H",
  //   column4: `Hydrogen is a chemical element with symbol H and atomic number 1. With a standard
  //       atomic weight of 1.008, hydrogen is the lightest element on the periodic table.`,
  // },
  // {
  //   column1: "Hydrogen",
  //   column2: "hjgfh",
  //   column3: "H",
  //   column4: `Hydrogen is a chemical element with symbol H and atomic number 1. With a standard
  //       atomic weight of 1.008, hydrogen is the lightest element on the periodic table.`,
  // },
  // {
  //   column1: "Hydrogen",
  //   column2: "hjgfh",
  //   column3: "H",
  //   column4: `Hydrogen is a chemical element with symbol H and atomic number 1. With a standard
  //       atomic weight of 1.008, hydrogen is the lightest element on the periodic table.`,
  // },
  // {
  //   column1: "Hydrogen",
  //   column2: "hjgfh",
  //   column3: "H",
  //   column4: `Hydrogen is a chemical element with symbol H and atomic number 1. With a standard
  //       atomic weight of 1.008, hydrogen is the lightest element on the periodic table.`,
  // },
]
@Component({
  selector: 'app-users',
  standalone: true,
  imports: [CommonModule, MatNativeDateModule,
    ReactiveFormsModule, MatExpansionModule, NgxLoadingModule, MatCardModule, MatFormFieldModule,MatIconModule,MatTableModule],
  templateUrl: './users.component.html',
  styleUrls: ['./users.component.scss'],
  animations: [
    trigger("detailExpand", [
      state("collapsed", style({ height: "0px", minHeight: "0" })),
      state("expanded", style({ height: "*" })),
      transition(
        "expanded <=> collapsed",
        animate("225ms cubic-bezier(0.4, 0.0, 0.2, 1)")
      ),
    ]),
  ],
})
export class UsersComponent implements OnInit {
  dataSource : any[] = [];
  columnsToDisplay = ["action", "firstName", "gender", "maidenName", "phone"];
  expandedElement!: PeriodicElement | null;
  user!: FormGroup
  currentlyOpenedItemIndex = -1;
  loading: boolean = false;
  usersData: any
  // items = [
  //   { header: 'Column 1' },
  //   { header: 'Column 2' },
  //   { header: 'Column 3' },
  //   { header: 'Column 4' },
  //   { header: 'Column 5' },
  //   { header: 'Column 6' },
  //   { header: 'Column 7' },
  //   { header: 'Column 8' },
  //   { header: 'Column 9' },
  //   { header: 'Column 10' },
  // ];
  uservalue: any;
  constructor(private fb: FormBuilder, private userApi: UserService) {
  }

  ngOnInit(): void {
    this.userForm();
    this.getUser();
  }

  userForm() {
    this.user = this.fb.group({
      firstName: [],
      gender: [],
      maidenName: [],
      phone: [],
    })
  }

  getUser() {
    this.userApi.get().subscribe(res => {
      this.usersData = res
      for (var user of this.usersData.users) {
        this.uservalue = user
        this.dataSource = user;
        console.log(this.uservalue)
      }
      this.user.patchValue({
        firstName: this.uservalue.firstName,
        maidenName: this.uservalue.maidenName,
        gender: this.uservalue.gender,
        phone: this.uservalue.phone
      })
    })
  }

  setOpened(itemIndex: any) {
    console.log(itemIndex)
    this.currentlyOpenedItemIndex = itemIndex;
  }

  setClosed(itemIndex: any) {
    if (this.currentlyOpenedItemIndex === itemIndex) {
      this.currentlyOpenedItemIndex = -1;
    }
  }

  showSpinner() {
    this.loading = true;
    setTimeout(() => { this.loading = false; }, 2000);
  }
}
